mapper.select:
    	SELECT * 
		FROM (
			SELECT ROWNUM RN, A.* 
				FROM (
						select * from NOTIFICATION_BBS order by noti_no DESC 
						) A
				)
		WHERE RN BETWEEN #{start} AND #{end}

SELECT * 
FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				select * from qna ORDER BY QNA_NO DESC 
				) A
		)
WHERE RN BETWEEN #{start} AND #{end}
